class HelloWorld {
	name: string;
	ary: any;

	constructor(name: string){
		this.name = name;

		// for loop same as C language
		for (let i=1; i<10; i++) {
			this.ary.push(i)
		}
	}
	greet(): void {
		console.log(`Hello, ${this.name}`);
	}
}

let greeting = new HelloWorld("Alberta");
greeting.greet();